package space.taran.arkmemo

import junit.framework.TestCase.assertEquals
import org.junit.Test
import space.taran.arkmemo.utils.SVG

class SVGUtilsTest {

    @Test
    fun parses_correctly() {
        val svg = SVG.parse(svgString)
        assertEquals(svg.get(), svgString)
    }

    @Test
    fun paths_are_correct() {
        val svg = SVG.parse(svgString)
        val svg1 = SVG.parse(svgString1)
        assertEquals(1, svg.getTestPaths().size)
        assertEquals(12, svg1.getTestPaths().size)
    }

